config.script:
return new Element('button', {
    "class":"toggle-dock-form",
    events:{
        click:function(){
            if(document.body.hasClass('body-overlayed')){
                document.body.removeClass('body-overlayed');
                wizard.viewer.getElement().addClass('dock-left')

                return;
            }
            document.body.addClass('body-overlayed');
            wizard.viewer.getElement().removeClass('dock-left')


        }
    }
})
