config.onRenderTextFieldScript:
var isMain=false;
textField.addInputFilter(function(text){
    if(text.indexOf('<main>')>=0){
        text= text.split('<main>').pop();
        isMain=true;
    }
    return text;
});
textField.addOutputFilter(function(text){
    if(isMain){
        return '<main>'+text;
    }
});
