config.script:
/*Project Menu*/

return new NavigationMenuModule({
      "Project":[
        {
          html:"Overview",
        },
        {
          html:"Tasks",
        },
        {
          html:"Files",
        },
        {
          html:"Discussion"
        },
        {  
           html:"Team",
           name:"Users"
        },
        // {
        //   html:"Timesheets"
        // }
         {
           html:"Map"
         }
      ] 
    },{
        "class":"inline-navigation-menu",
        targetUIView:function(button, section, viewer){
            return viewer.getChildView('content',1);
        },
        templateView:function(button, section){
            return button.view||("single"+section+button.html+"Detail");
        },
        buttonClass:function(button, section){
            return button["class"]||("menu-"+section.toLowerCase()+"-"+button.html.toLowerCase());
        },
        parentMenu:application.getNamedValue('navigationController'),
        initialView:{view:"Overview", section:"Project"},
        urlComponent:function(stub, button){

            var current=application.getNamedValue("currentProject");
            if(!current){
                return stub;
            }

            return 'Project-'+application.getNamedValue("currentProject").getId()+'/'+stub
        }
    });
