config.script:
/*Project Menu*/

var projectMenuController=new NavigationMenuModule({
      "Project":[
        {
          html:"Overview",
        },
        {
          html:"Tasks",
        },
        {
          html:"Files",
        },
        {
          html:"Discussion",
          formatEl:function(li){
              var p=item.data.discussion.posts;
              if(p>0){
                  li.addClass('withPosts');
                  li.setAttribute('data-counter',p);
              }

          }
        },
        {  
           html:"Team",
           name:"Users"
        },
        // {
        //   html:"Timesheets"
        // }
         {
           html:"Map"
         }
      ] 
    },{
        "class":"inline-navigation-menu",
        targetUIView:function(button, section, viewer){
            return viewer.getChildView('content',1);
        },
        templateView:function(button, section){
            return button.view||("single"+section+(button.name||button.html)+"Detail");
        },
        buttonClass:function(button, section){
            return button["class"]||("menu-"+section.toLowerCase()+"-"+button.html.toLowerCase());
        },
        parentMenu:application.getNamedValue('navigationController'),
        initialView:{view:"Overview", section:"Project"},
        menuId:"projectMenu"
    });

   application.setNamedValue('projectMenuController', projectMenuController);

   return projectMenuController;
