config.script:
var content=new Element('div');


var proponentFlow=content.appendChild(new Element('ul',{"class":"flow"}));

var last=null;
var appendStep=function(name, options){

    var el= proponentFlow.appendChild(new Element('li', options||{}));
    el.setAttribute('data-label',name);
    if(last){
        last.appendChild(new Element('span'));
    }

    last=el;
    return el;
}





appendStep("Quality Assessment", {"class":"user"});
appendStep("Prioritization", {"class":"current mail"});
appendStep("Meeting schedules", {"class":"user"});
appendStep("Dispute resolution");
appendStep("Reporting");


return content;
