config.onRenderModuleFn:
module.runOnceOnLoad(function(){
    module.getTextField().getInputElement().addClass('discussion-textbox');
    module.getTextField().addEvent('onEnterKey',function(e){
       if(!e.shiftKey){
            var value=module.getTextField().getValue();
            value=value.trim();
            module.getTextField().setValue('');
            if(value&&value!==""){

                console.log("send "+value);

            }

       }   
    });

});
