config.script:
var title= 'Project submission';
var content=new Element('div');


content.appendChild(new Element('div', {html:title}));
var proponentFlow=content.appendChild(new Element('ul',{"class":"flow"}));

var last=null;
var appendStep=function(name, options){

    var el= proponentFlow.appendChild(new Element('li', options||{}));
    el.setAttribute('data-label',name);
    if(last){
        last.appendChild(new Element('span'));
    }

    last=el;
    return el;
}


proponentFlow.appendChild(new Element('label',{html:"Proponent Flow"}));



appendStep("Project submission", {"class":"complete"});
appendStep("Validation", {"class":"current"});
appendStep("Initial Review");
appendStep("...");
appendStep("...");


return content;
