config.script:
/*File*/


/*
 figure out file type and render module
*/

var file=item;
if(typeof file!=="string"){

    //just to support passing object with id or file param. instead of a plain file path.

    if(typeof file.id=="string"){
        file=file.id;
    }
    if(typeof file.file=="string"){
        file=file.file;
    }
    if(typeof file.html=="string"){
        var urls=Proposal.ParseHtmlUrls(file.html);
        if(urls.length){
            file=urls[0];
        }


    }
}

if(typeof file=="string"){
    var mod = new ElementModule('div',{"class":"file-container"});
    var container=mod.getElement();

    var type=JSTextUtilities.ParseUrlMime(file);
    if(type){
        type=type.split('/')[0];

    }else{
        type='document';
    }

    if((['audio', 'video', 'image', 'document']).indexOf(type)>=0){

        if(type=='audio'){
            (new AudioModule({
                textQuery: function(callback) {
                    callback(file);
                }
            })).load(null, container, null);
        }
        if(type=='video'){
            (new VideoModule({
                textQuery: function(callback) {
                    callback(file);
                },
                width:100,
                height:100
            })).load(null, container, null);
        }
        if(type=='image'){
            (new ImageModule({
                textQuery: function(callback) {
                    callback(file);
                },
                width:100,
                height:100
            })).load(null, container, null);
        }
        if(type=='document'){
            (new DocumentModule({
                textQuery: function(callback) {
                    callback(file);
                },
                width:100,
                height:100
            })).load(null, container, null);
        }

        return mod; 
    }



}



return null;
