config.resolveItemScript:
if(item instanceof Project){

    callback(new ProjectList({
							"label":"Collection Datasets",
			                "showCreateBtn":true,
			                projects:function(callback){
			                	callback([item].concat(item.getProjectObjects()));
			                }
						}));


 }

config.getItemListScript:

if(item.getProjectList){
    item.getProjectList(callback);
    return;
}





ProjectTeam.CurrentTeam().runOnceOnLoad(function(team){
    var projects=team.getProjects();
    if(!application.getNamedValue("currentProject")){
       application.setNamedValue("currentProject", projects[0]);
   }
   callback(projects)
})
