config.script:
return null;
var ReserveQuery = new Class({
            Extends: AjaxControlQuery,
            initialize: function() {
                this.parent(CoreAjaxUrlRoot, 'get_reserve_metadata', {
                    plugin: 'ReferralManagement',
                    id:item.getId()
                });
            }
        });

var ReserveModule=new Class({
Extends:Module,
process:function(){

  var me=this;
   (new ReserveQuery()).addEvent('success',function(response){

      if(response.result.thumbnail){
          me.node.appendChild(new Asset.image(response.result.thumbnail,{
             styles:{
width:response.result.thumbnailWidth+"px",height:response.result.thumbnailHeight+"px"
               }
          }))
      }

       me.fireEvent('load');
   }).execute();


}

});


return new ReserveModule(application, {});
